COLLECTIONS = namedtuple('COLLECTIONS', ['inventory', 'cpes', 'cves'])('inventory', 'cpes', 'cves')


def create_db():
    insert_docs_in_collection(INVENTORY, COLLECTIONS.inventory)
    insert_docs_in_collection(CPES, COLLECTIONS.cpes)
    insert_docs_in_collection(CVES, COLLECTIONS.cves)


def insert_docs_in_collection(docs, collection):
    db = Database()
    if is_collection_empty(db, collection):
        for doc in docs:
            db.insert_document_in_collection(doc, collection)


def is_collection_empty(db, collection):
    return db.get_number_of_documents_in_collection(collection) == 0

